import React from "react";
import "../styles/experience.css";
import CardExperience from "../atoms/card-experience";
import List from "../atoms/list";

export default function Experience() {
  const experienceDesc = [
    {
      title: "IT Staff @PT Baraya Sukses Makmur",
      date: "",
      desc: [
        "Responsible for networking, installation and maintenance service",
        " Configuration and testing any new hardware",
        "Provided IT support services, troubleshooting hardware or software",
        "Maintenance and developing company website ",
      ],
    },
    {
      title: "Front-end dan Back-end Web Developer @ DOFF Project",
      date: "",
      desc: [
        "Responsible for networking, installation and maintenance service",
        " Configuration and testing any new hardware",
        "Provided IT support services, troubleshooting hardware or software",
        "Maintenance and developing company website ",
      ],
    },
  ];

  return (
    <div
      className="container mx-auto mt-32 grid justify-center w-2/4"
      id="experience"
    >
      <div
        data-aos="fade-up-right"
        data-aos-easing="linear"
        data-aos-duration="1000"
      >
        <div className="title">
          <h3 className="text-3xl font-semibold flex">
            <span className="title-about__number"> 02. </span> Where I've Worked
            <span className="strip"> </span>
          </h3>
        </div>
        {experienceDesc.map((item) => (
          <CardExperience title={item.title} date={"Jan 2018 - Mei 2018"}>
            <List>
              Responsible for networking, installation and maintenance service
            </List>
            <List>Configuration and testing any new hardware</List>
            <List>
              Provided IT support services, troubleshooting hardware or software
            </List>
            <List>Maintenance and developing company website</List>
          </CardExperience>
        ))}
        {/* 
        <CardExperience
          title={""}
          date={"Mei 2021 - Mei 2022"}
        >
          <List>Designing User Interface.</List>
          <List>Design Flow Process Back-end Application</List>
          <List>Designing the required database in the system </List>
          <List>System Integration Testing Application</List>
        </CardExperience> */}
      </div>
    </div>
  );
}
